    /**
     * Prepare testing payment
     * 
     * @param array $initialData
     * 
     * @return \FlexiPeeHP\Banka
     */
    public static function makeTestPayment($initialData = [], $dayBack = 1)
    {
        $yesterday = new \DateTime();
        $yesterday->modify('-'.$dayBack.' day');

        $testCode = 'PAY_'.\Ease\Functions::randomString();

        $payment = new \FlexiPeeHP\Banka($initialData);

        $payment->takeData(array_merge([
            'kod' => $testCode,
            'banka' => 'code:HLAVNI',
            'typPohybuK' => 'typPohybu.prijem',
            'popis' => 'FlexiPeeHP Test bank record',
            'varSym' => \Ease\Functions::randomNumber(1111, 9999),
            'specSym' => \Ease\Functions::randomNumber(111, 999),
            'bezPolozek' => true,
            'datVyst' => \FlexiPeeHP\RO::dateToFlexiDate($yesterday),
            'typDokl' => \FlexiPeeHP\RO::code('STANDARD')
                ], $initialData));
        if ($payment->sync()) {
            $payment->addStatusMessage($payment->getApiURL().' '.\FlexiPeeHP\RO::uncode($payment->getDataValue('typPohybuK')).' '.\FlexiPeeHP\RO::uncode($payment->getRecordIdent()).' '.\FlexiPeeHP\RO::uncode($payment->getDataValue('sumCelkem')).' '.\FlexiPeeHP\RO::uncode($payment->getDataValue('mena')),
                'success');
        } else {
            $payment->addStatusMessage(json_encode($payment->getData()), 'debug');
        }
        return $payment;
    }

